import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

const state = {
  showNav: true,
  token: null,
  username: '',
  userid: '',
  useravatar:'',
  message: [],
  messageRead:[],
};
export default new Vuex.Store({
  state,
})
